<?php/** * Created by PhpStorm. * User: NAGUI * Date: 20/11/2015 * Time: 16:04 */// liste des input to get$id=  filter_input(INPUT_GET,'id',FILTER_VALIDATE_INT);$AddPro=filter_input(INPUT_POST,'AddProd',FILTER_SANITIZE_NUMBER_INT);$ModPro=filter_input(INPUT_POST,'ModProd',FILTER_SANITIZE_NUMBER_INT);$Bread="ajouter";$Suppid=  filter_input(INPUT_GET,'SuppID',FILTER_VALIDATE_INT);if($Suppid!=""){       delete($Suppid,'products');       header('Location:ajouter');}if($AddPro==1):    $data=array(    "name"=>filter_input(INPUT_POST,'nom',FILTER_SANITIZE_STRING),    "categorie"=>filter_input(INPUT_POST,'catProd',FILTER_SANITIZE_STRING),    "public"=>1,    "cree_par"=>$_SESSION['user']['id'],    "modifier_par"=>0,    "created_at"=>date('Y-m-d H:i:s'),    );if(add($data,'products')){      $_SESSION['msg'] = "L'ajout du produit : ".$data['name']." est bien passer";      $_SESSION['type'] ="alert-success";} else{    $_SESSION['msg']="Une Erreur c'est produite";    $_SESSION['type']="alert-danger";      } endif;if($ModPro==1 && $id):    $data=array(    "name"=>filter_input(INPUT_POST,'nom',FILTER_SANITIZE_STRING),    "categorie"=>filter_input(INPUT_POST,'catProd',FILTER_SANITIZE_STRING),     "modifier_par"=>$_SESSION['user']['id'],    "created_at"=>date('Y-m-d H:i:s'),    );if(update($id,$data,'products')){      $_SESSION['msg'] = "La modification du produit : ".$data['name']." est bien passer";      $_SESSION['type'] ="alert-success";       header('Location:ajouter');} else{    $_SESSION['msg']="Une Erreur c'est produite";    $_SESSION['type']="alert-danger";      } endif;if($id){    $ProdModifier=get("*",'products',array('id='=>$id));    $Prodinf=$ProdModifier['reponse'][0];}$ListeProd=get('*','products');?><section class="content-header">    <h1>Gestion des produits </h1>  </section><!-- Main content --><section class="content">    <div class="row">                  <div class="col-md-4">  <form class="" method="post" action="">                 <div class="box box-primary">                    <div class="box-body box-profile">                        <div class="form-group">                            <label for="name">Nom</label>                            <input type="text" class="form-control" value="<?=$Prodinf['name']?>" name="nom" id="name" required>                        </div>                        <div class="form-group">                            <label for="prenom">Labo</label>                            <input type="text" class="form-control" value="<?=$Prodinf['categorie']?>" name="catProd" id="prodcat">                        </div>                        <? if(!$id): ?>                        <button type="submit" name="AddProd" value="1" class="btn btn-primary btn-block">Ajouter un produit</button>                        <?else:?>                        <button type="submit" name="ModProd" value="1" class="btn btn-warning btn-block">Modifier un produit</button>                        <?  endif;?>               </div><!-- /.box-body -->                </div>             </form>            </div>                         <div class="col-md-8 table-responsive">  <div class="box box-success">                    <div class="box-body box-profile">                <table class="table table-bordered table-condensed">                    <thead>                        <tr>                            <th>#</th>                            <th>Labos</th>                            <th>Produits</th>                            <th></th>                        </tr>                    </thead>                    <tbody>                        <?$ic=1; foreach ($ListeProd['reponse'] as $Prodc): ?>                        <tr>                            <td><?=$ic++?></td>                            <td><?=$Prodc['categorie']?></td>                            <td><?=$Prodc['name']?></td>                